tinymce.PluginManager.add('youtube', function(editor, url) {
  var openDialog = function () {
    return editor.windowManager.open({
      title: 'Вставка видео из YouTube',
      body: {
        type: 'panel',
        items: [
          {
            type: 'input',
            name: 'title',
            label: 'Наименование видео'
          },		
          {
            type: 'input',
            name: 'id_video',
            label: 'ID видео (только номер ID, например CTb9ZqUM2Hw)'
          },
          {
            type: 'input',
            name: 'facevideo',
            label: 'Ссылка на изображение для обложки видео'
          }		  
        ]
      },
      buttons: [
        {
          type: 'cancel',
          text: 'Close'
        },
        {
          type: 'submit',
          text: 'Save',
          primary: true
        }
      ],
      onSubmit: function (api) {
        var data = api.getData();
		if(data.facevideo == ''){
			data.facevideo = '/css/images/template/facevideo.jpg';
		}
        // Insert content when the window form is submitted
        //editor.insertContent('Title: ' + data.title);
		editor.insertContent('<div class="video embed-responsive embed-responsive-16by9"><a class="video__link" href="https://youtu.be/'+ data.id_video+'" target="_blank"><picture><img class="video__media img-fluid" src="'+data.facevideo+'" alt="'+data.title+'"></picture></a><button class="video__button" type="button" aria-label="Запустить видео"><svg width="68" height="48" viewBox="0 0 68 48"><path class="video__button-shape" d="M66.52,7.74c-0.78-2.93-2.49-5.41-5.42-6.19C55.79,.13,34,0,34,0S12.21,.13,6.9,1.55 C3.97,2.33,2.27,4.81,1.48,7.74C0.06,13.05,0,24,0,24s0.06,10.95,1.48,16.26c0.78,2.93,2.49,5.41,5.42,6.19 C12.21,47.87,34,48,34,48s21.79-0.13,27.1-1.55c2.93-0.78,4.64-3.26,5.42-6.19C67.94,34.95,68,24,68,24S67.94,13.05,66.52,7.74z"></path><path class="video__button-icon" d="M 45,24 27,14 27,34"></path></svg></button></div>');		
        api.close();
      }
    });
  };

  // Add a button that opens a window
  editor.ui.registry.addButton('youtube', {
    text: 'YouTube',
    onAction: function () {
      // Open window
      openDialog();
    }
  });

  // Adds a menu item, which can then be included in any menu via the menu/menubar configuration
  editor.ui.registry.addMenuItem('youtube', {
    text: 'Вставка видео из YouTube',
    onAction: function() {
      // Open window
      openDialog();
    }
  });

  return {
    getMetadata: function () {
      return  {
        name: "YouTube plugin",
        url: "http://exampleplugindocsurl.com"
      };
    }
  };
});
